import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import TimeForm from "../components/TimeForm";
import { Button } from "../components/ui/button";
import { useToast } from "../components/ui/use-toast";

const Time = () => {
  const navigate = useNavigate();
  const { toast } = useToast();
  const [savedConfig, setSavedConfig] = useState(null);

  useEffect(() => {
    // Load saved configuration from localStorage
    const savedTimeConfig = localStorage.getItem("timeConfig");
    if (savedTimeConfig) {
      try {
        setSavedConfig(JSON.parse(savedTimeConfig));
      } catch (error) {
        console.error("Error parsing saved time configuration:", error);
      }
    }
  }, []);

  const handleSubmit = (formValues) => {
    // Save the configuration to localStorage (already done in TimeForm)
    // Display success toast
    toast({
      title: "时间设置已保存",
      description: "您可以继续下一步配置",
      variant: "default",
    });

    // Update the saved config state
    setSavedConfig(formValues);
  };

  const handleNext = () => {
    navigate("/domain");
  };

  return (
    <div className="container mx-auto py-8">
      <div className="max-w-4xl mx-auto space-y-6">
        <div className="mb-2">
          <h1 className="text-3xl font-bold mb-2">时间配置</h1>
          <p className="text-muted-foreground">
            设置WRF模拟的时间范围和数据源
          </p>
        </div>

        <TimeForm onSubmit={handleSubmit} defaultValues={savedConfig} />

        <div className="flex justify-between">
          <Button variant="outline" onClick={() => navigate("/")}>
            返回首页
          </Button>
          <Button 
            onClick={handleNext} 
            disabled={!savedConfig}
          >
            下一步：区域设置
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Time;
