import React, { useState, useId, useEffect } from 'react';
import { ChevronDown } from "lucide-react";
import { Input } from "./ui/input";
import { Card } from "./ui/card";
import { Button } from "./ui/button";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "./ui/form";
import { Alert, AlertDescription, AlertTitle } from './ui/alert';
import { InfoIcon } from 'lucide-react';
import { Select, SelectItem } from './CustomSelect';

const WrfDomainForm = ({
  onSubmit,
  onError,
  defaultValues = {
    e_we: 100,
    e_sn: 100,
    dx: 30,
    dy: 30,
    ref_lat: 34.0,
    ref_lon: 118.0,
    truelat1: 30.0,
    truelat2: 60.0,
    stand_lon: 118.0,
    max_dom: 1,
    map_proj: "lambert",
  },
}) => {
  const id = useId();
  const [formValues, setFormValues] = useState({
    ...defaultValues,
    i_parent_start: defaultValues.i_parent_start || [0, 0, 0],
    j_parent_start: defaultValues.j_parent_start || [0, 0, 0],
    parent_grid_ratio: defaultValues.parent_grid_ratio || [1, 3, 3],
    parent_time_step_ratio: defaultValues.parent_time_step_ratio || [1, 3, 3],
  });
  const [errors, setErrors] = useState({});
  const [maxDomValue, setMaxDomValue] = useState(defaultValues.max_dom || 1);

  // Update form values when defaultValues change
  useEffect(() => {
    if (defaultValues) {
      setFormValues({
        ...defaultValues,
        i_parent_start: defaultValues.i_parent_start || [0, 0, 0],
        j_parent_start: defaultValues.j_parent_start || [0, 0, 0],
        parent_grid_ratio: defaultValues.parent_grid_ratio || [1, 3, 3],
        parent_time_step_ratio: defaultValues.parent_time_step_ratio || [1, 3, 3],
      });
      setMaxDomValue(defaultValues.max_dom || 1);
    }
  }, [defaultValues]);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    const processedValue = type === "number" ? (value === '' ? '' : parseFloat(value)) : value;

    setFormValues(prev => ({
      ...prev,
      [name]: processedValue,
    }));

    // Clear the error for this field when it's changed
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = {...prev};
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  const handleSelectChange = (name) => (value) => {
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: name === 'max_dom' ? Number(value) : value,
    }));

    if (name === 'max_dom') {
      setMaxDomValue(Number(value));
    }

    // Clear the error for this field when it's changed
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = {...prev};
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    // Required number fields
    ['e_we', 'e_sn', 'dx', 'dy', 'ref_lat', 'ref_lon', 'truelat1', 'truelat2', 'stand_lon'].forEach(field => {
      if (formValues[field] === undefined || formValues[field] === '') {
        newErrors[field] = '此字段不能为空';
      } else if (isNaN(Number(formValues[field]))) {
        newErrors[field] = '必须是一个数字';
      }
    });

    // Validate max_dom
    if (!formValues.max_dom) {
      newErrors.max_dom = '此字段不能为空';
    } else if (isNaN(Number(formValues.max_dom)) || Number(formValues.max_dom) < 1 || Number(formValues.max_dom) > 3) {
      newErrors.max_dom = '嵌套网格数必须在1-3之间';
    }

    // Validate map_proj
    if (!formValues.map_proj) {
      newErrors.map_proj = '请选择投影类型';
    }

    // Validate latitude values
    if (formValues.ref_lat < -90 || formValues.ref_lat > 90) {
      newErrors.ref_lat = '纬度值必须在-90到90度之间';
    }

    if (formValues.truelat1 < -90 || formValues.truelat1 > 90) {
      newErrors.truelat1 = '纬度值必须在-90到90度之间';
    }

    if (formValues.truelat2 < -90 || formValues.truelat2 > 90) {
      newErrors.truelat2 = '纬度值必须在-90到90度之间';
    }

    // Validate longitude values
    if (formValues.ref_lon < -180 || formValues.ref_lon > 180) {
      newErrors.ref_lon = '经度值必须在-180到180度之间';
    }

    if (formValues.stand_lon < -180 || formValues.stand_lon > 180) {
      newErrors.stand_lon = '经度值必须在-180到180度之间';
    }

    // Validate grid dimensions
    if (formValues.e_we < 3) {
      newErrors.e_we = '网格点数必须大于等于3';
    }

    if (formValues.e_sn < 3) {
      newErrors.e_sn = '网格点数必须大于等于3';
    }

    // Validate grid spacing
    if (formValues.dx <= 0) {
      newErrors.dx = '网格间距必须大于0';
    }

    if (formValues.dy <= 0) {
      newErrors.dy = '网格间距必须大于0';
    }

    setErrors(newErrors);

    // If there are errors and the onError callback exists, call it with the first error
    const errorKeys = Object.keys(newErrors);
    if (errorKeys.length > 0 && onError) {
      onError(`${errorKeys[0]}: ${newErrors[errorKeys[0]]}`);
    }

    return errorKeys.length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      if (onSubmit) {
        onSubmit(formValues);
      }
    }
  };

  return (
    <Card className="w-full p-6">
      <h2 className="text-2xl font-bold mb-6">WRF域配置</h2>

      <Alert className="mb-6">
        <InfoIcon className="h-4 w-4" />
        <AlertTitle>域配置重要提示</AlertTitle>
        <AlertDescription>
          请确保选择合适的投影类型和范围参数。Lambert投影适用于中纬度地区，Mercator投影适用于热带地区，Polar投影适用于极地地区。
        </AlertDescription>
      </Alert>

      <Form>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Grid dimensions */}
            <FormField
              name="e_we"
              render={() => (
                <FormItem>
                  <FormLabel>E_WE (西-东方向网格点数)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-e_we"}
                      type="number"
                      name="e_we"
                      value={formValues.e_we}
                      onChange={handleChange}
                      min="3"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.e_we ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.e_we && <p className="mt-1 text-sm text-red-600">{errors.e_we}</p>}
                </FormItem>
              )}
            />

            <FormField
              name="e_sn"
              render={() => (
                <FormItem>
                  <FormLabel>E_SN (南-北方向网格点数)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-e_sn"}
                      type="number"
                      name="e_sn"
                      value={formValues.e_sn}
                      onChange={handleChange}
                      min="3"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.e_sn ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.e_sn && <p className="mt-1 text-sm text-red-600">{errors.e_sn}</p>}
                </FormItem>
              )}
            />

            {/* Grid spacing */}
            <FormField
              name="dx"
              render={() => (
                <FormItem>
                  <FormLabel>DX (x方向网格间距, 千米)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-dx"}
                      type="number"
                      name="dx"
                      value={formValues.dx}
                      onChange={handleChange}
                      step="0.01"
                      min="0"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.dx ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.dx && <p className="mt-1 text-sm text-red-600">{errors.dx}</p>}
                </FormItem>
              )}
            />

            <FormField
              name="dy"
              render={() => (
                <FormItem>
                  <FormLabel>DY (y方向网格间距, 千米)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-dy"}
                      type="number"
                      name="dy"
                      value={formValues.dy}
                      onChange={handleChange}
                      step="0.01"
                      min="0"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.dy ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.dy && <p className="mt-1 text-sm text-red-600">{errors.dy}</p>}
                </FormItem>
              )}
            />

            {/* Reference coordinates */}
            <FormField
              name="ref_lat"
              render={() => (
                <FormItem>
                  <FormLabel>参考纬度 (度)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-ref_lat"}
                      type="number"
                      name="ref_lat"
                      value={formValues.ref_lat}
                      onChange={handleChange}
                      step="0.000001"
                      min="-90"
                      max="90"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.ref_lat ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.ref_lat && <p className="mt-1 text-sm text-red-600">{errors.ref_lat}</p>}
                </FormItem>
              )}
            />

            <FormField
              name="ref_lon"
              render={() => (
                <FormItem>
                  <FormLabel>参考经度 (度)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-ref_lon"}
                      type="number"
                      name="ref_lon"
                      value={formValues.ref_lon}
                      onChange={handleChange}
                      step="0.000001"
                      min="-180"
                      max="180"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.ref_lon ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.ref_lon && <p className="mt-1 text-sm text-red-600">{errors.ref_lon}</p>}
                </FormItem>
              )}
            />

            {/* Projection parameters */}
            <FormField
              name="truelat1"
              render={() => (
                <FormItem>
                  <FormLabel>第一标准纬度 (度)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-truelat1"}
                      type="number"
                      name="truelat1"
                      value={formValues.truelat1}
                      onChange={handleChange}
                      step="0.000001"
                      min="-90"
                      max="90"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.truelat1 ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.truelat1 && <p className="mt-1 text-sm text-red-600">{errors.truelat1}</p>}
                </FormItem>
              )}
            />

            <FormField
              name="truelat2"
              render={() => (
                <FormItem>
                  <FormLabel>第二标准纬度 (度)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-truelat2"}
                      type="number"
                      name="truelat2"
                      value={formValues.truelat2}
                      onChange={handleChange}
                      step="0.000001"
                      min="-90"
                      max="90"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.truelat2 ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.truelat2 && <p className="mt-1 text-sm text-red-600">{errors.truelat2}</p>}
                </FormItem>
              )}
            />

            <FormField
              name="stand_lon"
              render={() => (
                <FormItem>
                  <FormLabel>标准经度 (度)</FormLabel>
                  <FormControl>
                    <Input
                      id={id + "-stand_lon"}
                      type="number"
                      name="stand_lon"
                      value={formValues.stand_lon}
                      onChange={handleChange}
                      step="0.000001"
                      min="-180"
                      max="180"
                      className={`mt-1 block w-full rounded-md border ${
                        errors.stand_lon ? 'border-red-500' : 'border-gray-300'
                      } shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2`}
                    />
                  </FormControl>
                  {errors.stand_lon && <p className="mt-1 text-sm text-red-600">{errors.stand_lon}</p>}
                </FormItem>
              )}
            />

            <FormField
              name="max_dom"
              render={() => (
                <FormItem>
                  <FormLabel>最大域数量</FormLabel>
                  <FormControl>
                    <Select
                      defaultValue={maxDomValue.toString()}
                      onValueChange={handleSelectChange("max_dom")}
                      id={id + "-max_dom"}
                    >
                      <SelectItem value="1">1</SelectItem>
                      <SelectItem value="2">2</SelectItem>
                      <SelectItem value="3">3</SelectItem>
                    </Select>
                  </FormControl>
                  {errors.max_dom && <p className="mt-1 text-sm text-red-600">{errors.max_dom}</p>}
                </FormItem>
              )}
            />

            {/* Projection type */}
            <FormField
              name="map_proj"
              render={() => (
                <FormItem className="col-span-1 md:col-span-2">
                  <FormLabel>地图投影方式</FormLabel>
                  <FormControl>
                    <Select
                      defaultValue={formValues.map_proj}
                      onValueChange={handleSelectChange("map_proj")}
                      id={id + "-map_proj"}
                    >
                      <SelectItem value="lambert">Lambert Conformal</SelectItem>
                      <SelectItem value="polar">Polar Stereographic</SelectItem>
                      <SelectItem value="mercator">Mercator</SelectItem>
                      <SelectItem value="lat-lon">Lat-Lon</SelectItem>
                    </Select>
                  </FormControl>
                  <FormDescription>
                    选择WRF域的地图投影方式
                  </FormDescription>
                  {errors.map_proj && <p className="mt-1 text-sm text-red-600">{errors.map_proj}</p>}
                </FormItem>
              )}
            />
          </div>

          {maxDomValue > 1 && (
            <div className="p-4 bg-gray-50 rounded-md">
              <h3 className="font-medium text-lg mb-4">嵌套网格设置</h3>

              {/* Only display nested settings for domains 2 and 3 if selected */}
              {Array.from({ length: maxDomValue - 1 }, (_, i) => {
                const domainNum = i + 2; // Domain 2, 3, etc.
                return (
                  <div key={`domain-${domainNum}`} className="mb-6 p-4 border border-gray-200 rounded-md">
                    <h4 className="font-medium mb-3">网格 {domainNum} 设置</h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <label htmlFor={id + `-i_parent_start-${domainNum}`} className="block text-sm font-medium text-gray-700 mb-1">
                          I 父网格起始点
                        </label>
                        <input
                          id={id + `-i_parent_start-${domainNum}`}
                          name={`i_parent_start_${domainNum}`}
                          type="number"
                          value={formValues.i_parent_start[domainNum - 2]}
                          onChange={(e) => {
                            const newValues = [...formValues.i_parent_start];
                            newValues[domainNum - 2] = Number(e.target.value);
                            setFormValues({ ...formValues, i_parent_start: newValues });
                          }}
                          className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2"
                        />
                      </div>
                      <div>
                        <label htmlFor={id + `-j_parent_start-${domainNum}`} className="block text-sm font-medium text-gray-700 mb-1">
                          J 父网格起始点
                        </label>
                        <input
                          id={id + `-j_parent_start-${domainNum}`}
                          name={`j_parent_start_${domainNum}`}
                          type="number"
                          value={formValues.j_parent_start[domainNum - 2]}
                          onChange={(e) => {
                            const newValues = [...formValues.j_parent_start];
                            newValues[domainNum - 2] = Number(e.target.value);
                            setFormValues({ ...formValues, j_parent_start: newValues });
                          }}
                          className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2"
                        />
                      </div>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-3">
                      <div>
                        <label htmlFor={id + `-parent_grid_ratio-${domainNum}`} className="block text-sm font-medium text-gray-700 mb-1">
                          网格比例
                        </label>
                        <input
                          id={id + `-parent_grid_ratio-${domainNum}`}
                          name={`parent_grid_ratio_${domainNum}`}
                          type="number"
                          value={formValues.parent_grid_ratio[domainNum - 2]}
                          onChange={(e) => {
                            const newValues = [...formValues.parent_grid_ratio];
                            newValues[domainNum - 2] = Number(e.target.value);
                            setFormValues({ ...formValues, parent_grid_ratio: newValues });
                          }}
                          className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2"
                        />
                      </div>
                      <div>
                        <label htmlFor={id + `-parent_time_step_ratio-${domainNum}`} className="block text-sm font-medium text-gray-700 mb-1">
                          时间步长比例
                        </label>
                        <input
                          id={id + `-parent_time_step_ratio-${domainNum}`}
                          name={`parent_time_step_ratio_${domainNum}`}
                          type="number"
                          value={formValues.parent_time_step_ratio[domainNum - 2]}
                          onChange={(e) => {
                            const newValues = [...formValues.parent_time_step_ratio];
                            newValues[domainNum - 2] = Number(e.target.value);
                            setFormValues({ ...formValues, parent_time_step_ratio: newValues });
                          }}
                          className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2"
                        />
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}

          <div className="flex justify-end">
            <Button type="submit">保存配置</Button>
          </div>
        </form>
      </Form>
    </Card>
  );
};

export default WrfDomainForm;
