import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import DomainForm from "../components/DomainForm";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "../components/ui/card";
import { Alert, AlertDescription, AlertTitle } from "../components/ui/alert";
import { Button } from "../components/ui/button";
import { InfoIcon } from "lucide-react";
import { useToast } from "../components/ui/use-toast";
import { DEFAULT_CONFIG } from "../utils/constants";

const Domain = () => {
  const [loading, setLoading] = useState(true);
  const [config, setConfig] = useState(null);
  const navigate = useNavigate();
  const { toast } = useToast();

  useEffect(() => {
    try {
      const savedConfig = localStorage.getItem("wrf_config");
      if (savedConfig) {
        setConfig(JSON.parse(savedConfig));
      } else {
        setConfig(DEFAULT_CONFIG);
      }
    } catch (error) {
      console.error("Error loading configuration:", error);
      setConfig(DEFAULT_CONFIG);
    } finally {
      setLoading(false);
    }
  }, []);

  const handleDomainSubmit = (domainData) => {
    // Update only the domain part in the full configuration
    const updatedConfig = {
      ...config,
      domain: domainData,
    };

    // Save the updated configuration
    localStorage.setItem("wrf_config", JSON.stringify(updatedConfig));
    setConfig(updatedConfig);

    toast({
      title: "配置已保存",
      description: "域设置已成功保存",
    });
  };

  const handleBack = () => {
    navigate("/time");
  };

  const handleNext = () => {
    navigate("/physics");
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-2">加载中...</h2>
          <p className="text-muted-foreground">正在获取域配置信息</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-6 max-w-4xl">
      <h1 className="text-3xl font-bold mb-2">域设置</h1>
      <p className="text-muted-foreground mb-6">配置WRF模型的计算域</p>

      <Alert className="mb-6">
        <InfoIcon className="h-4 w-4" />
        <AlertTitle>配置信息</AlertTitle>
        <AlertDescription>
          在这一步中，您将配置WRF模型的计算域参数，包括嵌套域的数量、分辨率和网格点数等。
        </AlertDescription>
      </Alert>

      <Card className="mb-6">
        <CardHeader>
          <CardTitle>域参数设置</CardTitle>
          <CardDescription>设置WRF模型的计算域参数</CardDescription>
        </CardHeader>
        <CardContent>
          <DomainForm 
            onSubmit={handleDomainSubmit} 
            defaultValues={config?.domain || {}}
          />
        </CardContent>
      </Card>

      <div className="flex justify-between mt-8">
        <Button
          variant="outline"
          onClick={handleBack}
        >
          返回
        </Button>

        <Button
          onClick={handleNext}
          disabled={!config?.domain}
        >
          下一步: 物理参数化
        </Button>
      </div>
    </div>
  );
};

export default Domain;
