import React from "react";
import { ArrowRight, Cloud, Clock, Compass, CloudLightning, Layers, BarChart3, LayoutGrid, Globe, Thermometer, Waves, Check, AreaChart } from "lucide-react";
import { Link } from "react-router-dom";
import { Button } from "../components/ui/button";
import { Badge } from "../components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "../components/ui/card";
import Layout from '../components/Layout';

const FeatureCard = ({ title, description, icon }) => {
  return (
    <Card className="flex flex-col p-6 space-y-4 border border-border/50 bg-background/50 hover:bg-accent/5 transition-colors">
      <div className="p-2 w-12 h-12 rounded-md bg-primary/10 flex items-center justify-center text-primary">
        {icon}
      </div>
      <h3 className="text-xl font-semibold">{title}</h3>
      <p className="text-muted-foreground">{description}</p>
    </Card>
  );
};

const HeroSection = ({
  title,
  description,
  badge,
  actions,
  features,
}) => {
  return (
    <section className="bg-background text-foreground py-12 sm:py-24 px-4">
      <div className="mx-auto max-w-5xl flex flex-col gap-16">
        <div className="flex flex-col items-center gap-6 text-center">
          {/* Badge */}
          {badge && (
            <Badge variant="outline" className="gap-2">
              <span className="text-muted-foreground">{badge.text}</span>
            </Badge>
          )}

          {/* Title */}
          <h1 className="text-4xl font-semibold leading-tight sm:text-5xl md:text-6xl max-w-3xl">
            {title}
          </h1>

          {/* Description */}
          <p className="text-md max-w-[650px] font-medium text-muted-foreground sm:text-xl">
            {description}
          </p>

          {/* Actions */}
          <div className="flex flex-wrap justify-center gap-4 mt-4">
            {actions.map((action, index) => (
              <Button key={index} variant={action.variant || "default"} size="lg" asChild>
                <Link to={action.href} className="flex items-center gap-2">
                  {action.text}
                  {index === 0 && <ArrowRight className="h-4 w-4" />}
                </Link>
              </Button>
            ))}
          </div>
        </div>

        {/* Features Section */}
        <div className="flex flex-col gap-8">
          <h2 className="text-2xl sm:text-3xl font-semibold text-center">
            主要功能
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {features.map((feature, index) => (
              <FeatureCard
                key={index}
                title={feature.title}
                description={feature.description}
                icon={feature.icon}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

const Home = () => {
  const features = [
    {
      title: '时间设置',
      description: '配置模拟开始、结束时间及时间步长',
      icon: <Clock className="h-6 w-6" />,
      href: '/time'
    },
    {
      title: '区域设置',
      description: '配置模拟区域的地理范围和网格分辨率',
      icon: <Globe className="h-6 w-6" />,
      href: '/domain'
    },
    {
      title: '物理参数化',
      description: '选择微物理、积云、行星边界层等物理方案',
      icon: <Thermometer className="h-6 w-6" />,
      href: '/physics'
    },
    {
      title: '动力学设置',
      description: '配置模型的动力学核心参数',
      icon: <Waves className="h-6 w-6" />,
      href: '/dynamics'
    },
    {
      title: '配置审核',
      description: '检查并确认所有配置参数',
      icon: <Check className="h-6 w-6" />,
      href: '/review'
    },
    {
      title: '输出生成',
      description: '生成WRF配置文件并查看模拟结果',
      icon: <AreaChart className="h-6 w-6" />,
      href: '/output'
    }
  ];

  const actions = [
    {
      title: '开始配置',
      href: '/time'
    },
    {
      title: '查看示例',
      href: '/examples'
    }
  ];

  return (
    <Layout>
      <div className="py-12 space-y-12">
        {/* Hero Section */}
        <div className="text-center space-y-5">
          <h1 className="text-4xl font-bold md:text-5xl">简化天气研究和预报模型配置</h1>
          <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
            沃风平台为WRF模型提供直观的图形界面，简化配置过程，减少错误，加速部署。
          </p>

          {/* Action Buttons */}
          <div className="flex flex-wrap justify-center gap-4 mt-8">
            {actions.map((action, index) => (
              <Button key={index} asChild size="lg">
                <Link to={action.href}>{action.title}</Link>
              </Button>
            ))}
          </div>
        </div>

        {/* Features Grid */}
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {features.map((feature, index) => (
            <Card key={index} className="transition-all hover:shadow-md">
              <Link to={feature.href}>
                <CardHeader>
                  <div className="flex items-center gap-2">
                    <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center text-primary">
                      {feature.icon}
                    </div>
                    <CardTitle>{feature.title}</CardTitle>
                  </div>
                </CardHeader>
                <CardContent>
                  <CardDescription className="text-base">{feature.description}</CardDescription>
                </CardContent>
              </Link>
            </Card>
          ))}
        </div>
      </div>
    </Layout>
  );
};

export default Home;
