import React from "react";
import { ArrowRight, Cloud } from "lucide-react";
import { Link } from "react-router-dom";
import { Button } from "../components/ui/button";
import { Badge } from "../components/ui/badge";
import { Card } from "../components/ui/card";
import Layout from '../components/Layout';

const FeatureCard = ({ title, description, icon }) => {
  return (
    <Card className="flex flex-col p-6 space-y-4 border border-border/50 bg-background/50 hover:bg-accent/5 transition-colors">
      <div className="p-2 w-12 h-12 rounded-md bg-primary/10 flex items-center justify-center text-primary">
        {icon}
      </div>
      <h3 className="text-xl font-semibold">{title}</h3>
      <p className="text-muted-foreground">{description}</p>
    </Card>
  );
};

const HeroSection = ({
  title,
  description,
  badge,
  actions,
  features,
}) => {
  return (
    <section className="bg-background text-foreground py-12 sm:py-24 px-4">
      <div className="mx-auto max-w-5xl flex flex-col gap-16">
        <div className="flex flex-col items-center gap-6 text-center">
          {/* Badge */}
          {badge && (
            <Badge variant="outline" className="gap-2">
              <span className="text-muted-foreground">{badge.text}</span>
            </Badge>
          )}

          {/* Title */}
          <h1 className="text-4xl font-semibold leading-tight sm:text-5xl md:text-6xl max-w-3xl">
            {title}
          </h1>

          {/* Description */}
          <p className="text-md max-w-[650px] font-medium text-muted-foreground sm:text-xl">
            {description}
          </p>

          {/* Actions */}
          <div className="flex flex-wrap justify-center gap-4 mt-4">
            {actions.map((action, index) => (
              <Button key={index} variant={action.variant || "default"} size="lg" asChild>
                <Link to={action.href} className="flex items-center gap-2">
                  {action.text}
                  {index === 0 && <ArrowRight className="h-4 w-4" />}
                </Link>
              </Button>
            ))}
          </div>
        </div>

        {/* Features Section */}
        <div className="flex flex-col gap-8">
          <h2 className="text-2xl sm:text-3xl font-semibold text-center">
            主要功能
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {features.map((feature, index) => (
              <FeatureCard
                key={index}
                title={feature.title}
                description={feature.description}
                icon={feature.icon}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

const Home = () => {
  const features = [
    {
      title: "时间设置",
      description: "配置模拟开始和结束时间，以及时间步长等参数",
      icon: <Clock className="h-6 w-6" />,
      href: '/time'
    },
    {
      title: "区域设置",
      description: "设置模拟区域的网格、分辨率和地理位置参数",
      icon: <Compass className="h-6 w-6" />,
      href: '/domain'
    },
    {
      title: "物理参数化",
      description: "配置微物理、辐射、表面层等物理参数化方案",
      icon: <CloudLightning className="h-6 w-6" />,
      href: '/physics'
    },
    {
      title: "动力学设置",
      description: "设置动力学选项，包括扩散、阻尼和非静力学选项",
      icon: <Layers className="h-6 w-6" />,
      href: '/dynamics'
    },
    {
      title: "配置审核",
      description: "检查所有配置参数并确认最终设置",
      icon: <BarChart3 className="h-6 w-6" />,
      href: '/review'
    },
    {
      title: "输出生成",
      description: "生成namelist文件和可视化配置结果",
      icon: <LayoutGrid className="h-6 w-6" />,
      href: '/output'
    },
  ];

  const actions = [
    {
      title: "开始配置",
      href: "/time",
      variant: "default"
    },
    {
      title: "了解更多",
      href: "#",
      variant: "outline"
    }
  ];

  return (
    <Layout>
      <div className="w-full">
        {/* Hero Section */}
        <div className="container mx-auto max-w-screen-lg py-12 px-4 md:px-6">
          <div className="text-center mb-12">
            <Badge variant="outline" className="mb-4">
              WRF配置助手
            </Badge>
            <h1 className="scroll-m-20 text-4xl font-extrabold tracking-tight lg:text-5xl mb-4">
              简化天气研究和预报模型配置
            </h1>
            <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
              通过简单易用的界面快速配置WRF模型，无需编辑复杂的namelist文件
            </p>
          </div>

          {/* Action Buttons */}
          <div className="flex flex-wrap justify-center gap-4 mb-12">
            {actions.map((action, index) => (
              <Link to={action.href} key={index}>
                <Button variant={action.variant} size="lg" className="gap-2">
                  {action.title}
                  {index === 0 && <ArrowRight className="h-4 w-4" />}
                </Button>
              </Link>
            ))}
          </div>

          {/* Features Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mt-16">
            {features.map((feature, index) => (
              <Link to={feature.href} key={index} className="block">
                <div className="p-6 border border-border rounded-lg hover:shadow-md transition-shadow bg-card">
                  <div className="mb-4 p-2 bg-primary/10 rounded-md w-fit">
                    {feature.icon}
                  </div>
                  <h3 className="text-xl font-bold mb-2">{feature.title}</h3>
                  <p className="text-muted-foreground">{feature.description}</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Home;
