import React, { useState } from "react";
import { useId } from "react";
import { Card } from "./ui/card";
import { Label } from "./ui/label";
import { Button } from "./ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "./CustomSelect";
import { Alert, AlertDescription, AlertTitle } from "./ui/alert";
import { InfoIcon } from "lucide-react";
import { PHYSICS_OPTIONS } from "../utils/constants";

const PhysicsForm = ({ onSubmit, defaultValues = {
  mp_physics: 6,
  ra_lw_physics: 1,
  ra_sw_physics: 1,
  sf_surface_physics: 2,
  bl_pbl_physics: 1,
  cu_physics: 1
} }) => {
  const [formValues, setFormValues] = useState(defaultValues);
  const [errors, setErrors] = useState({});
  const mpPhysicsId = useId();
  const raLwPhysicsId = useId();
  const raSwPhysicsId = useId();
  const sfSurfacePhysicsId = useId();
  const blPblPhysicsId = useId();
  const cuPhysicsId = useId();

  const handleChange = (name, value) => {
    setFormValues({
      ...formValues,
      [name]: parseInt(value)
    });

    // Clear error for this field if it exists
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: undefined
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    let isValid = true;

    // Validate each required field
    if (formValues.mp_physics === undefined) {
      newErrors.mp_physics = "请选择微物理方案";
      isValid = false;
    }

    if (formValues.ra_lw_physics === undefined) {
      newErrors.ra_lw_physics = "请选择长波辐射方案";
      isValid = false;
    }

    if (formValues.ra_sw_physics === undefined) {
      newErrors.ra_sw_physics = "请选择短波辐射方案";
      isValid = false;
    }

    if (formValues.sf_surface_physics === undefined) {
      newErrors.sf_surface_physics = "请选择地表物理方案";
      isValid = false;
    }

    if (formValues.bl_pbl_physics === undefined) {
      newErrors.bl_pbl_physics = "请选择边界层方案";
      isValid = false;
    }

    if (formValues.cu_physics === undefined) {
      newErrors.cu_physics = "请选择积云方案";
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm() && onSubmit) {
      onSubmit(formValues);
    }
  };

  return (
    <Card className="w-full max-w-4xl p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-foreground">WRF物理参数设置</h2>
        <p className="text-sm text-muted-foreground mt-1">
          选择适合您模拟需求的物理参数化方案
        </p>
      </div>

      <Alert className="mb-6">
        <InfoIcon className="h-4 w-4" />
        <AlertTitle>物理参数化配置说明</AlertTitle>
        <AlertDescription>
          物理参数化方案的选择对WRF模拟结果有显著影响。请根据您的模拟区域和研究目标选择合适的方案。
          <ul className="list-disc pl-5 mt-2 space-y-1">
            <li><span className="font-medium">微物理方案</span> - 控制云和降水过程</li>
            <li><span className="font-medium">辐射方案</span> - 处理短波和长波辐射传输</li>
            <li><span className="font-medium">地表方案</span> - 处理陆地表面过程</li>
            <li><span className="font-medium">边界层方案</span> - 模拟近地层湍流和热量传输</li>
            <li><span className="font-medium">积云方案</span> - 模拟次网格对流过程</li>
          </ul>
        </AlertDescription>
      </Alert>

      <form onSubmit={handleSubmit}>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="space-y-2">
            <Label htmlFor={mpPhysicsId}>微物理方案 (mp_physics)</Label>
            <Select 
              defaultValue={formValues.mp_physics.toString()} 
              onValueChange={(value) => handleChange('mp_physics', value)}
            >
              <SelectTrigger id={mpPhysicsId} className="[&_[data-desc]]:hidden">
                <SelectValue placeholder="选择微物理方案" />
              </SelectTrigger>
              <SelectContent>
                {Object.entries(PHYSICS_OPTIONS.mp_physics).map(([value, label]) => (
                  <SelectItem key={value} value={value}>
                    {label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.mp_physics && (
              <p className="text-sm text-red-500 mt-1">{errors.mp_physics}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor={raLwPhysicsId}>长波辐射 (ra_lw_physics)</Label>
            <Select 
              defaultValue={formValues.ra_lw_physics.toString()} 
              onValueChange={(value) => handleChange('ra_lw_physics', value)}
            >
              <SelectTrigger id={raLwPhysicsId} className="[&_[data-desc]]:hidden">
                <SelectValue placeholder="选择长波辐射方案" />
              </SelectTrigger>
              <SelectContent>
                {Object.entries(PHYSICS_OPTIONS.ra_lw_physics).map(([value, label]) => (
                  <SelectItem key={value} value={value}>
                    {label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.ra_lw_physics && (
              <p className="text-sm text-red-500 mt-1">{errors.ra_lw_physics}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor={raSwPhysicsId}>短波辐射 (ra_sw_physics)</Label>
            <Select 
              defaultValue={formValues.ra_sw_physics.toString()} 
              onValueChange={(value) => handleChange('ra_sw_physics', value)}
            >
              <SelectTrigger id={raSwPhysicsId} className="[&_[data-desc]]:hidden">
                <SelectValue placeholder="选择短波辐射方案" />
              </SelectTrigger>
              <SelectContent>
                {Object.entries(PHYSICS_OPTIONS.ra_sw_physics).map(([value, label]) => (
                  <SelectItem key={value} value={value}>
                    {label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.ra_sw_physics && (
              <p className="text-sm text-red-500 mt-1">{errors.ra_sw_physics}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor={sfSurfacePhysicsId}>地表物理 (sf_surface_physics)</Label>
            <Select 
              defaultValue={formValues.sf_surface_physics.toString()} 
              onValueChange={(value) => handleChange('sf_surface_physics', value)}
            >
              <SelectTrigger id={sfSurfacePhysicsId} className="[&_[data-desc]]:hidden">
                <SelectValue placeholder="选择地表物理方案" />
              </SelectTrigger>
              <SelectContent>
                {Object.entries(PHYSICS_OPTIONS.sf_surface_physics).map(([value, label]) => (
                  <SelectItem key={value} value={value}>
                    {label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.sf_surface_physics && (
              <p className="text-sm text-red-500 mt-1">{errors.sf_surface_physics}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor={blPblPhysicsId}>边界层方案 (bl_pbl_physics)</Label>
            <Select 
              defaultValue={formValues.bl_pbl_physics.toString()} 
              onValueChange={(value) => handleChange('bl_pbl_physics', value)}
            >
              <SelectTrigger id={blPblPhysicsId} className="[&_[data-desc]]:hidden">
                <SelectValue placeholder="选择边界层方案" />
              </SelectTrigger>
              <SelectContent>
                {Object.entries(PHYSICS_OPTIONS.bl_pbl_physics).map(([value, label]) => (
                  <SelectItem key={value} value={value}>
                    {label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.bl_pbl_physics && (
              <p className="text-sm text-red-500 mt-1">{errors.bl_pbl_physics}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor={cuPhysicsId}>积云方案 (cu_physics)</Label>
            <Select 
              defaultValue={formValues.cu_physics.toString()} 
              onValueChange={(value) => handleChange('cu_physics', value)}
            >
              <SelectTrigger id={cuPhysicsId} className="[&_[data-desc]]:hidden">
                <SelectValue placeholder="选择积云方案" />
              </SelectTrigger>
              <SelectContent>
                {Object.entries(PHYSICS_OPTIONS.cu_physics).map(([value, label]) => (
                  <SelectItem key={value} value={value}>
                    {label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.cu_physics && (
              <p className="text-sm text-red-500 mt-1">{errors.cu_physics}</p>
            )}
          </div>
        </div>

        <div className="flex justify-end mt-6">
          <Button type="submit">保存物理参数设置</Button>
        </div>
      </form>
    </Card>
  );
};

export default PhysicsForm;
