import React from "react";
import { ArrowRight, Cloud, Terminal } from "lucide-react";
import { Link } from "react-router-dom";
import { Button } from "../components/ui/button";
import { Badge } from "../components/ui/badge";
import { Card } from "../components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "../components/ui/tabs";

const FeatureCard = ({ title, description, icon }) => {
  return (
    <Card className="flex flex-col p-6 space-y-4 border border-border/50 bg-background/50 hover:bg-accent/5 transition-colors">
      <div className="p-2 w-12 h-12 rounded-md bg-primary/10 flex items-center justify-center text-primary">
        {icon}
      </div>
      <h3 className="text-xl font-semibold">{title}</h3>
      <p className="text-muted-foreground">{description}</p>
    </Card>
  );
};

// 示例namelist配置
const shareExample = `&share
 wrf_core = 'ARW',
 max_dom = 1,
 start_date = '2025-02-14_00:00:00',
 end_date   = '2025-02-15_00:00:00',
 interval_seconds = 10800,
 io_form_geogrid = 102,
/

&geogrid
 parent_id         =   1,
 parent_grid_ratio =   1, 
 i_parent_start    =   1, 
 j_parent_start    =   1,
 e_we              =   31, 
 e_sn              =   31,
 geog_data_res = 'default',
 dx = 3000,
 dy = 3000,
 map_proj = 'lambert',
 ref_lat   =  36.20,
 ref_lon   =  120.60,
 truelat1  =  30.0,
 truelat2  =  60.0,
 stand_lon =  120.60,
 geog_data_path = '/home/onemule/WRF/run/geo_data/geog_high_res_mandatory/WPS_GEOG'
/

&ungrib
 out_format = 'WPS',
 prefix = 'FILE',
/

&metgrid
 fg_name = 'FILE',
 io_form_metgrid = 102,
/`;

const timeControlExample = `&time_control
 run_days                            = 0,
 run_hours                           = 24,
 run_minutes                         = 0,
 run_seconds                         = 0,
 start_year                          = 2025,
 start_month                         = 02,
 start_day                           = 14,
 start_hour                          = 00,
 end_year                            = 2025,
 end_month                           = 02,
 end_day                             = 15,
 end_hour                            = 00,
 interval_seconds                    = 10800,
 input_from_file                     = .true.,
 history_interval                    = 60,
 restart                             = .false.,
 frames_per_outfile                  = 1000,
